# Definição da classe TV

class TV:

    cor = 'preta'

    # Método construtor __init__
    def __init__(self, tamanho_tv):
        # Atributos da classe
        self.ligada = False  # Atributo ligada definido como False por padrão (TV desligada)
        self.tamanho = tamanho_tv  # Atributo tamanho da TV definido pelo parâmetro tamanho_tv
        self.canal = "Netflix"  # Atributo canal definido como "Netflix" por padrão
        self.volume = 10  # Atributo volume definido como 10 por padrão

    # Método para mudar o canal
    def mudar_canal(self, novo_canal):
        self.canal = novo_canal  # Define o canal
        print(f'Canal alterado para: "{novo_canal}".')

# Programa principal
# Instanciando dois objetos da classe TV com tamanhos diferentes
tv_sala = TV(tamanho_tv=55)  # TV da sala é instanciada com tamanho 55
tv_quarto = TV(tamanho_tv=77)  # TV do quarto é instanciada com tamanho 77

# Exibindo a cor atual da TV da sala
print(tv_sala.cor)  # Imprime a cor da TV da sala (deve imprimir 'branca')

# Alterando o canal da TV da sala para "Globo" usando o método mudar_canal()
tv_sala.mudar_canal("Globo")

# Exibindo o canal atual da TV da sala e do quarto
print(tv_sala.canal)  # Imprime o canal atual da TV da sala (deve imprimir 'Globo')
print(tv_quarto.canal)  # Imprime o canal atual da TV do quarto (deve imprimir 'Netflix')

print(tv_sala.cor)
print(tv_quarto.cor)

"""Prática -  sistema bancário"""

from datetime import datetime
import pytz #timezone
from random import randint


class ContaCorrente():
    '''
    Padrão de docstring PEP 257:

    Cria um objeto ContaCorrente para gerenciar as contas dos clientes

    Atributos:
        nome (str): Nome do titular da conta.
        _cpf (str): Número do CPF do titular da conta. Deve ser inserido com pontos e traços.
        _saldo (float): Saldo atual da conta.
        _limite (float): Limite de saldo negativo permitido (padrão: -1000).
        _agencia (str): Número da agência bancária.
        _num_conta (str): Número da conta corrente.
        _transacoes (list): Lista de tuplas representando as transações realizadas.
            Cada tupla contém (valor, descrição, data e hora).
    '''

    @staticmethod
    def _data_hora():
        fuso_br = pytz.timezone('Brazil/East')
        horario_BR = datetime.now(fuso_br)
        return horario_BR.strftime('%d/%m/%Y %H:%M:%S')

    def __init__(self, nome, _cpf, agencia, num_conta):
        self.nome = nome
        self._cpf = _cpf
        self._saldo = 0
        self._limite = None
        self.agencia = agencia  # Alteração aqui: referência ao atributo '_agencia'
        self.num_conta = num_conta
        self._transacoes = []
        self._cartoes = []

    def consultar_saldo(self):
        print(f"Seu saldo atual é: {self._saldo:,.2f}")

    def depositar(self, valor):
        self._saldo += valor
        self._transacoes.append((valor, 'Saldo: {}'.format(self._saldo), ContaCorrente._data_hora()))

    def _limite_conta(self):
        self._limite = -1000
        return self._limite

    def sacar_dinheiro(self, valor):
        if self._saldo - valor < self._limite_conta():
            print('Você não tem saldo suficiente para sacar esse valor')
        else:
            self._saldo -= valor
            self._transacoes.append((-valor, 'Saldo: {}'.format(self._saldo), ContaCorrente._data_hora()))

    def consultar_limite_cheque_especial(self):
        print('Seu limite de Cheque Especial é de R${:,.2f}'.format(self._limite_conta()))

    def consultar_historico_transacoes(self):
        print('Histórico de transações:')
        print('Valor, Saldo, Data e Hora')
        for transacao in self._transacoes:
            print(transacao)

    def transferir(self, valor, conta_destino):
        self._saldo -= valor
        self._transacoes.append((-valor, 'Saldo: {}'.format(self._saldo), ContaCorrente._data_hora()))
        conta_destino._saldo += valor
        conta_destino._transacoes.append((valor, conta_destino._saldo, ContaCorrente._data_hora()))

class CartaoCredito:

    @staticmethod
    def _data_hora():
        fuso_BR = pytz.timezone('Brazil/East')
        horario_BR = datetime.now(fuso_BR)
        return horario_BR

    def __init__(self, titular, conta_corrente):
        self.numero = 123
        self.titular = titular
        self.validade = '{}/{}'.format(CartaoCredito._data_hora().month, CartaoCredito._data_hora().year + 4)
        self.cod_seguranca = '{}{}{}'.format(randint(0, 9), randint(0, 9), randint(0, 9))
        self.limite = 1000
        self._senha = '1234'
        self.conta_corrente = conta_corrente
        conta_corrente._cartoes.append(self)

    #método get (getter)
    @property
    def senha(self):
        return self._senha

    #método set (setter)
    @senha.setter
    def senha(self, valor):
        if len(valor) == 4 and valor.isnumeric():
            self._senha = valor
        else:
            print("Nova senha inválida!")

from random import randint

class Agencia:

    def __init__(self, telefone, cnpj, numero):
        self.telefone = telefone
        self.cnpj = cnpj
        self.numero = numero
        self.clientes = []
        self.caixa = 0
        self.emprestimos = []

    def verificar_caixa(self):
        if self.caixa < 1000000:
            print("Caixa atual abaixo do nível recomendado: R$ {}.".format(self.caixa))
        else:
            print("O valor de Caixa está dentro dos padrões. Caixa atual: R$ {}.".format(self.caixa))

    def emprestar_dinheiro(self, valor, cpf, juros):
        if self.caixa > valor:
            self.emprestimos.append((valor, cpf, juros))
        else:
            print("Empréstino indiponível por falta de valor em caixa.")

    def adicionar_cliente(self, nome, cpf, patrimonio):
        self.clientes.append((nome, cpf, patrimonio))

#Subclasses

class AgenciaVirtual(Agencia):

    def __init__(self, site, telefone, cnpj):
        self.site = site
        super().__init__(telefone, cnpj, 1000)
        self.caixa = 1000000
        self.caixa_paypal = 0

    def depositar_paypal(self, valor):
        self.caixa -= valor
        self.caixa_paypal += valor

    def sacar_paypal(self, valor):
        self.caixa_paypal -= valor
        self.caixa += valor


class AgenciaComum(Agencia):

    def __init__(self, telefone, cnpj):
        super().__init__(telefone, cnpj, numero=(1001, 9999))
        self.caixa = 1000000


class AgenciaPremium(Agencia):

    def __init__(self, telefone, cnpj):
        super().__init__(telefone, cnpj, numero=randint(1001, 9999))
        self.caixa = 10000000

    def adicionar_cliente(self, nome, cpf, patrimonio):
        if patrimonio > 1000000:
            super().adicionar_cliente(nome, cpf, patrimonio)
        else:
            print('O Cliente não tem o patrimônio mínimo necessário para entrar na Agência Premium.')

